import React, { useState } from "react";
import { AiOutlineSearch, AiFillRedditCircle } from "react-icons/ai";
import "./SearchBar.css";

const SearchBar = () => {
  const [input, setInput] = useState("");

  const handleChange = (event) => {
    setInput(event.target.value);
    console.log(event.target.value);
  };
  const handleClick = () => {
    // on click then get then search
    //   pass a data set with 2 items, one is the site, and one is the date
    console.log(input);
  };

  return (
    <div className="SearchContainer">
      <div className="SearchBarContainer">
        <div className="input-group">
          <input
            onChange={handleChange}
            type="text"
            className="form-control search-bar"
          ></input>
          <button
            onClick={handleClick}
            type="button"
            className="btn btn-primary search-icon"
          >
            <AiOutlineSearch />
          </button>
        </div>
      </div>

      <div className="SearchButtonsContainer">

      <div class="custom-control custom-checkbox">
        <input type="checkbox" class="custom-control-input" id="defaultUnchecked"></input>
        <label class="custom-control-label" for="defaultUnchecked">Default unchecked</label>
      </div>

      {/* Default checked */}
      <div class="custom-control custom-checkbox">
        <input type="checkbox" class="custom-control-input" id="defaultChecked" checked></input>
        <label class="custom-control-label" for="defaultChecked">Default checked</label>
      </div>

        <button className="btn btn-outline-secondary btn-lg search-btn">
          <AiFillRedditCircle className="search-btn-icon" />
          Reddit
        </button>
        <button className="btn btn-outline-secondary btn-lg search-btn">
          <AiFillRedditCircle className="search-btn-icon" />
          StackOverflow
        </button>
        <button className="btn btn-outline-secondary btn-lg search-btn">
          <AiFillRedditCircle className="search-btn-icon" />
          Amazon
        </button>
      </div>
    </div>
  );
};

export default SearchBar;
