import React, { useState } from "react";
import ButtonGroup from 'react-bootstrap/ButtonGroup'
import ToggleButton from 'react-bootstrap/ToggleButton'
import { AiOutlineSearch, AiFillRedditCircle } from "react-icons/ai";
import "./SearchBar.css";

const SearchBar = () => {
  const [input, setInput] = useState("");
  const [radioValue, setRadioValue] = useState('1');

  const radios = [
    { name: 'Reddit', value: '1' , icon:'AiOutlineSearch'},
    { name: 'Radio', value: '2' , icon:'AiFillRedditCircle'},
    { name: 'Radio', value: '3' , icon:'AiFillRedditCircle'},
  ];

  const handleChange = (event) => {
    setInput(event.target.value);
    console.log(event.target.value);
  };
  const handleClick = () => {
    // on click then get then search
    //   pass a data set with 2 items, one is the site, and one is the date
    console.log(input);
  };

  return (
    <div className="SearchContainer">
      <div className="SearchBarContainer">
        <div className="input-group">
          <input
            onChange={handleChange}
            type="text"
            className="form-control search-bar"
          ></input>
          <button
            onClick={handleClick}
            type="button"
            className="btn btn-primary search-icon"
          ><AiOutlineSearch/>
          </button>
        </div>
      </div>

      <div className="SearchButtonsContainer">

      <ButtonGroup className="mb-2 search-btn">
        {radios.map((radio, idx) => (
          <ToggleButton
            key={idx}
            id={`radio-${idx}`}
            type="radio"
            variant="secondary"
            name="radio"
            value={radio.value}
            checked={radioValue === radio.value}
            onChange={(e) => setRadioValue(e.currentTarget.value)}
          >
            {radio.icon === "AiOutlineSearch" &&
                <AiOutlineSearch/>
            }
          </ToggleButton>
        ))}
      </ButtonGroup>

      </div>
    </div>
  );
};

export default SearchBar;
